import React, {useState, useEffect, useContext} from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/Profile.css';
import { globalContext } from '../../App';

function PatientProfile({ isOwnProfile }) {
  const navigate = useNavigate();


  const {globalState, setGlobalState} = useContext(globalContext);

  useEffect(()=>{
    console.log("empty dep array");
    const appState = localStorage.getItem('globalState');
    if(appState){
      setGlobalState(JSON.parse(appState));
    }
  }, []);

  useEffect(()=>{
    // we need to do this but once 
      console.log("28 marks in CS725. FML")
  }, [globalState]);


  // Mocked patient data
  const patientData = {
    name: 'John Doe',
    email: 'john.doe@example.com',
    medical_id: '123456789',
    photo: '', // Set this to a photo URL if available, or leave as an empty string for the placeholder
  };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };


  return (
    <div className="patient-profile-container">
      {/* <div className="dashboard-navbar">

        <h2 className="dashboard-title">Book an appointment</h2>
        <div className='dashboard-navbar-right'>
          <button className='dashboard-button' onClick={handlesPatientLogout}>
          <LogoutIcon/>
          </button>
        </div>
      </div> */}
      <div className="patient-profile-content">
        <div className="patient-profile-image-container">
          {patientData.photo ? (
            <img src={patientData.photo} alt="Patient Profile" className="patient-profile-image" />
          ) : (
            <div className="patient-profile-no-image">No Photo</div>
          )}
        </div>
        <div className="patient-profile-info">
          <div className="patient-profile-row">
            <label>Name:</label>
            <p>{patientData.name}</p>
          </div>
          <div className="patient-profile-row">
            <label>Contact:</label>
            <p>{patientData.email}</p>
          </div>
          <div className="patient-profile-row">
            <label>Medical ID:</label>
            <p>{patientData.medical_id}</p>
          </div>
          {isOwnProfile && (
            <button className="patient-edit-button" onClick={handleEditProfile}>
              Edit Profile
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default PatientProfile;
