import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/Dashboard.css';
import {jwtDecode} from 'jwt-decode'; // Ensure you have installed jwt-decode with `npm install jwt-decode`

function DoctorDashboard() {
  const [appointments, setAppointments] = useState([]);
  const navigate = useNavigate();
  const [message, setMessage] = useState('');

  // Function to decode the JWT and get doctor ID
  const getDoctorIdFromJWT = () => {
    try {
      const token = localStorage.getItem('doctor_access_token'); // Replace with your token key
      if (!token) {
        setMessage('You are not authenticated. Please log in.');
        return null;
      }
      const decoded = jwtDecode(token);
      console.log('Decoded Token:', decoded);
      return decoded.sub.id; // Adjust based on your JWT payload structure
    } catch (error) {
      console.error('Error decoding JWT:', error);
      setMessage('Invalid authentication token.');
      return null;
    }
  };

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('patient_access_token'); // Get the token
        const id = getDoctorIdFromJWT(); // Get the doctor ID from the token

        if (!id || !token) {
          console.error('Missing doctor ID or token');
          return;
        }

        // Make the API call with the JWT token in the headers
        const response = await fetch(`http://127.0.0.1:5000/api/appointments/doctor/${id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`, // Add the JWT token
          },
        });

        if (response.ok) {
          const data = await response.json();
          setAppointments(data.appointments || []);
        } else {
          console.error('Failed to fetch appointments.');
        }
      } catch (error) {
        console.error('Error fetching appointments:', error);
      }
    };

    fetchAppointments();
  }, []);

  const handleEdit = (appointmentId) => {
    navigate(`/edit-appointment/${appointmentId}`);
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">Doctor Dashboard - Appointments</h2>
      {appointments.length > 0 ? (
        <div className="dashboard-buttons">
          {appointments.map((appointment, index) => (
            <div className="dashboard-button" key={appointment.id}>
              <div>
                <strong>Sl. No.:</strong> {index + 1}
              </div>
              <div>
                <strong>Name:</strong> {appointment.name}
              </div>
              <div>
                <strong>Date:</strong> {new Date(appointment.date).toLocaleDateString()}
              </div>
              <button
                className="dashboard-button"
                onClick={() => handleEdit(appointment.id)}
              >
                Edit
              </button>
            </div>
          ))}
        </div>
      ) : (
        <div className="dashboard-button no-appointments">No appointments available.</div>
      )}
    </div>
  );
}

export default DoctorDashboard;
