import React, {useState, useEffect, useContext} from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/Profile.css';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import LogoutIcon from '@mui/icons-material/Logout';
import { globalContext } from '../../App';


const DefaultState = {
  name: "",
  access_token: "",
  blood_group: "",
  blood_pressure: "",
  date_of_birth: "",
  created: "",
  height: "",
  id: "",
  medical_history: "",
  photos: "",
  weight: "",
  email: "",
  medical_id: '123456789',
}


function PatientProfile() {
  const navigate = useNavigate();


  const {globalState, setGlobalState} = useContext(globalContext);


  const {formData, setFormData} = useState(DefaultState);

  useEffect(()=>{
    console.log("empty dep array");
    const appState = localStorage.getItem('globalState');
    if(appState){
      setGlobalState(JSON.parse(appState));
      setFormData(JSON.parse(appState));
    }
  }, []);

  useEffect(()=>{
    // we need to do this but once 
      console.log("28 marks in CS725. FML")
  }, [globalState]);


  // Mocked patient data
  // const patientData = {
  //   name: 'John Doe',
  //   email: 'john.doe@example.com',
  //   medical_id: '123456789',
  //   photo: '', // Set this to a photo URL if available, or leave as an empty string for the placeholder
  // };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Submitted: ", formData);
  };


  const handlesPatientLogout = ()=>{
    let storedUserJWT = localStorage.getItem('patient_access_token');
    if(storedUserJWT){
      localStorage.removeItem('patient_access_token');
    }
    setGlobalState((state)=>{
      return (
        DefaultState
      )
    });

    navigate('/');
  }
  const handlesBackButton = () => {
    navigate('/patient-dashboard');
  }
  const styles = {
    container: {
      maxWidth: "600px",
      margin: "50px auto",
      padding: "20px",
      border: "1px solid #ddd",
      borderRadius: "8px",
      backgroundColor: "#fff",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    },
    backButton: {
      background: "none",
      border: "none",
      fontSize: "24px",
      cursor: "pointer",
      color: "#006400",
      marginBottom: "20px",
    },
    form: {
      display: "flex",
      flexDirection: "column",
      gap: "20px",
    },
    photoContainer: {
      alignSelf: "center",
    },
    photoPlaceholder: {
      width: "150px",
      height: "150px",
      border: "1px solid #ccc",
      borderRadius: "8px",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "16px",
      color: "#aaa",
      backgroundColor: "#f5f5f5",
    },
    inputGroup: {
      display: "flex",
      flexDirection: "column",
    },
    label: {
      marginBottom: "5px",
      fontSize: "16px",
      fontWeight: "bold",
    },
    input: {
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "4px",
      fontSize: "16px",
    },
    inputReadOnly: {
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "4px",
      fontSize: "16px",
      backgroundColor: "#f9f9f9",
      color: "#555",
    },
    textarea: {
      padding: "10px",
      border: "1px solid #ccc",
      borderRadius: "4px",
      fontSize: "16px",
      minHeight: "80px",
    },
    submitButton: {
      padding: "12px",
      border: "none",
      borderRadius: "4px",
      backgroundColor: "#006400",
      color: "white",
      fontSize: "16px",
      cursor: "pointer",
    },
    submitButtonHover: {
      backgroundColor: "#004d00",
    },
  };
  return (
    <div className="patient-profile-container">
      {/* <div className="dashboard-navbar">

        <h2 className="dashboard-title">Book an appointment</h2>
        <div className='dashboard-navbar-right'>
          <button className='dashboard-button' onClick={handlesPatientLogout}>
          <LogoutIcon/>
          </button>
        </div>
      </div> */}
      <div className="dashboard-navbar">
      <div className='dashboard-navbar-right'>
          <button className='dashboard-button' onClick={handlesBackButton}>
            <ArrowBackIcon />
          </button>
          </div>
        <h2 className="dashboard-title">Welcome, {globalState['name']}</h2>
        <div className='dashboard-navbar-right'>
          <button className='dashboard-button' onClick={handlesPatientLogout}>
          <LogoutIcon/>
          </button>
        </div>
      </div>
      <div style={styles.container}>
      <button style={styles.backButton}>←</button>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.photoContainer}>
          <div style={styles.photoPlaceholder}>No Photo</div>
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            style={styles.input}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Contact:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            style={styles.input}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Medical ID:</label>
          <input
            type="text"
            name="medical_id"
            value={formData.medical_id}
            readOnly
            style={styles.inputReadOnly}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Body Weight:</label>
          <input
            type="number"
            name="weight"
            value={formData.weight}
            onChange={handleChange}
            style={styles.input}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Height:</label>
          <input
            type="number"
            name="height"
            value={formData.height}
            onChange={handleChange}
            style={styles.input}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Blood Group:</label>
          <input
            type="text"
            name="blood_group"
            value={formData.blood_group}
            onChange={handleChange}
            style={styles.input}
          />
        </div>

        <div style={styles.inputGroup}>
          <label style={styles.label}>Medical History:</label>
          <textarea
            name="medical_history"
            value={formData.medical_history}
            onChange={handleChange}
            style={styles.textarea}
          />
        </div>

        <button type="submit" style={styles.submitButton}>
          Edit Profile
        </button>
      </form>
    </div>
    </div>
  );
}

export default PatientProfile;
